package fmi.spotify.media.model;

public record PlaylistDTO(
        Long id,
        String name,
        String description,
        String coverUrl) {

    static String COVER_URL = "data:image/jpeg;base64,/9 j/4 AAQSkZJRgABAQAAAQABAAD/2 ";

    public static PlaylistDTO fromPlaylist(Playlist playlist) {
        return new PlaylistDTO(
                playlist.getId(),
                playlist.getName(),
                playlist.getDescription(),
                COVER_URL);
    }
}